# This is a tutorial using web3.py

# prepare web3
# pip install web3

# web3 test with local providers
from web3 import Web3

# IPCProvider:
w3 = Web3(Web3.IPCProvider('./path/to/geth.ipc'))

# HTTPProvider:
w3 = Web3(Web3.HTTPProvider('http://127.0.0.1:8545'))

# WebsocketProvider:
w3 = Web3(Web3.WebsocketProvider('wss://127.0.0.1:8546'))

w3.is_connected()  # True if successful

# get blockchain info
w3.eth.get_block('latest')

